using System.Collections;using System.Collections.Generic;using UnityEngine;public class FXVSinPos : MonoBehaviour{    public float amplitude = 2.0f;    public float speed = 1.0f;    public Vector3 vector = Vector3.up;    private Vector3 startPos;    private float angle = 0.0f;	void Start ()    {        startPos = transform.position;    }	void Update ()    {        angle += Time.deltaTime * speed;        if (angle > Mathf.PI * 2.0f)            angle -= Mathf.PI * 2.0f;        transform.position = startPos + vector * Mathf.Sin(angle) * amplitude;    }}